import React, { createContext, useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { Navbar, Nav, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import Home from './pages/Home';
import AddSales from './pages/AddSales';
import Sales from './pages/Sales';
import SalesDetail from './pages/SalesDetail';
import TotalSales from './pages/TotalSales';
import AddPurchase from './pages/AddPurchase';
import Purchase from './pages/Purchase';
import Remaining from './pages/Remaining';
import StoreBalance from './pages/StoreBalance';
import Navb from './pages/Navbar';
import TotalPurchase from './pages/TotalPurchase';

export const AppContext = createContext();

const App = () => {
  const [sales, setSales] = useState(() => {
    const savedSales = localStorage.getItem('sales');
    return savedSales ? JSON.parse(savedSales) : [];
  });
  const [purchases, setPurchases] = useState(() => {
    const savedPurchases = localStorage.getItem('purchases');
    return savedPurchases ? JSON.parse(savedPurchases) : [];
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [alertMessage, setAlertMessage] = useState('');

  useEffect(() => {
    localStorage.setItem('sales', JSON.stringify(sales));
    localStorage.setItem('purchases', JSON.stringify(purchases));
  }, [sales, purchases]);

  const calculateTotals = (items, includeShippedOnly = false) => {
    const sizes = ['8mm', '10mm', '12mm', '14mm', '16mm', '20mm', '24mm', '32mm'];
    return sizes.reduce((acc, size) => {
      acc[size] = items.reduce((sum, item) => {
        if (!includeShippedOnly) {
          return sum + (item.quantities?.[size] || 0);
        } else {
          const shipped = item.shippedQuantities?.[size] || 0;
          return sum + (item.shipped ? shipped : 0);
        }
      }, 0);
      return acc;
    }, {});
  };

  const purchasedTotals = calculateTotals(purchases, false);
  const soldTotals = calculateTotals(sales, true);
  const balances = Object.keys(purchasedTotals).reduce((acc, size) => {
    acc[size] = Math.max(0, purchasedTotals[size] - (soldTotals[size] || 0));
    return acc;
  }, {});


  return (
    <AppContext.Provider value={{ sales, setSales, purchases, setPurchases, searchTerm, setSearchTerm, alertMessage, setAlertMessage, purchasedTotals, soldTotals, balances }}>
      <Router>
<Navbar bg="light" expand="lg" className="py-2 border-bottom">
          <Container>
            <Navbar.Brand as={Link}  style={{color:'green'}}>Yegna Trading</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav" className="justify-content-center">
              <Nav className="mx-auto">
                <Nav.Link as={Link} to="/">Home</Nav.Link>
                <Nav.Link as={Link} to="/add-sales">Add Sales</Nav.Link>
                <Nav.Link as={Link} to="/sales">Sales</Nav.Link>
                <Nav.Link as={Link} to="/total-sales">Total Sales</Nav.Link>
                <Nav.Link as={Link} to="/detail"></Nav.Link>
                <Nav.Link as={Link} to="/add-purchase">Add Purchase</Nav.Link>
                <Nav.Link as={Link} to="/purchase">Purchase</Nav.Link>
                <Nav.Link as={Link} to="/TotalPurchase">Total-Purchase</Nav.Link>
                <Nav.Link as={Link} to="/remaining">Remaining</Nav.Link>
                <Nav.Link as={Link} to="/store">Store (Balance)</Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>


        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/add-sales" element={<AddSales />} />
          <Route path="/sales" element={<Sales />} />
          <Route path="/sales/:id" element={<SalesDetail />} />
          <Route path="/total-sales" element={<TotalSales />} />
          <Route path="/add-purchase" element={<AddPurchase />} />
          <Route path="/purchase" element={<Purchase />} />
          <Route path="/remaining" element={<Remaining />} />
          <Route path="/TotalPurchase" element={<TotalPurchase/>} />
          <Route path="/store" element={<StoreBalance />} />
        </Routes>
      </Router>
    </AppContext.Provider>
  );
};

export default App;
