import React, { useContext, useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Table, Button, Alert, Form } from 'react-bootstrap';
import { AppContext } from '../App';

const SalesDetail = () => {
  const { id } = useParams();
  const { sales, setSales, setAlertMessage } = useContext(AppContext);
  const navigate = useNavigate();
  const saleIndex = sales.findIndex(s => s.id === parseInt(id));
  const sale = sales[saleIndex];
  const [shippedQuantities, setShippedQuantities] = useState({});
  const [isEditing, setIsEditing] = useState(false);
  const [editFormData, setEditFormData] = useState({});

  useEffect(() => {
    if (sale) {
      setShippedQuantities(sale.shippedQuantities || {});
      setEditFormData(sale);
    }
  }, [sale]);

  const handleShippedChange = (size, value) => {
    const shipped = parseInt(value) || 0;
    const remaining = sale.quantities[size] - (sale.shippedQuantities?.[size] || 0);
    if (shipped <= remaining) {
      setShippedQuantities({ ...shippedQuantities, [size]: shipped });
    }
  };

  const handleShip = () => {
    const newSales = [...sales];
    const updatedSale = { ...newSales[saleIndex] };
    let allShipped = true;
    Object.keys(shippedQuantities).forEach(size => {
      const shipped = shippedQuantities[size] || 0;
      const previouslyShipped = updatedSale.shippedQuantities?.[size] || 0;
      const remaining = updatedSale.quantities[size] - previouslyShipped;
      if (shipped <= remaining && shipped > 0) {
        updatedSale.shippedQuantities = updatedSale.shippedQuantities || {};
        updatedSale.shippedQuantities[size] = previouslyShipped + shipped;
        updatedSale.quantities[size] -= shipped;
        if (updatedSale.quantities[size] > 0) allShipped = false;
      }
    });
    updatedSale.shipped = allShipped;
    newSales[saleIndex] = updatedSale;
    setSales(newSales);
    setAlertMessage('Shipped and subtracted from store');
    setTimeout(() => {
      setAlertMessage('');
      navigate('/sales');
    }, 3000);
  };

  const handleDelete = () => {
    if (window.confirm('Are you sure you want to delete this sale?')) {
      const newSales = [...sales];
      newSales.splice(saleIndex, 1);
      setSales(newSales);
      setAlertMessage('Sale deleted');
      setTimeout(() => {
        setAlertMessage('');
        navigate('/sales');
      }, 3000);
    }
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditFormData({ ...editFormData, [name]: value });
  };

  const handleEditQuantityChange = (e) => {
    const { name, value } = e.target;
    setEditFormData({
      ...editFormData,
      quantities: { ...editFormData.quantities, [name]: parseInt(value) || 0 },
    });
  };

  const handleSaveEdit = () => {
    const newSales = [...sales];
    newSales[saleIndex] = { ...editFormData };
    setSales(newSales);
    setIsEditing(false);
    setAlertMessage('Sale updated');
    setTimeout(() => setAlertMessage(''), 3000);
  };

  if (!sale) return <Container><p>Sale not found</p></Container>;

  return (
    <Container className="mt-5">
      <h2>Sales Detail - {sale.customer}</h2>
      {alertMessage && <Alert variant="success">{alertMessage}</Alert>}
      {isEditing ? (
        <Form>
          <Form.Group className="mb-3">
            <Form.Label>Customer Name</Form.Label>
            <Form.Control type="text" name="customer" value={editFormData.customer} onChange={handleEditChange} required />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Plate Number</Form.Label>
            <Form.Control type="text" name="plate" value={editFormData.plate} onChange={handleEditChange} required />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>FS Number</Form.Label>
            <Form.Control type="text" name="fs" value={editFormData.fs} onChange={handleEditChange} required />
          </Form.Group>
          <Row>
            {Object.keys(editFormData.quantities).map((size) => (
              <Col md={3} key={size}>
                <Form.Group className="mb-3">
                  <Form.Label>{size}</Form.Label>
                  <Form.Control type="number" name={size} value={editFormData.quantities[size]} onChange={handleEditQuantityChange} min="0" />
                </Form.Group>
              </Col>
            ))}
          </Row>
          <Form.Group className="mb-3">
            <Form.Label>Date</Form.Label>
            <Form.Control type="date" name="date" value={editFormData.date} onChange={handleEditChange} required />
          </Form.Group>
          <Button variant="primary" onClick={handleSaveEdit}>Save</Button>
          <Button variant="secondary" onClick={() => setIsEditing(false)} className="ms-2">Cancel</Button>
        </Form>
      ) : (
        <>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Size</th>
                <th>Ordered</th>
                <th>Remaining</th>
                <th>Shipped (This Time)</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(sale.quantities).map((size) => {
                const previouslyShipped = sale.shippedQuantities?.[size] || 0;
                const remaining = sale.quantities[size] - previouslyShipped;
                return (
                  <tr key={size}>
                    <td>{size}</td>
                    <td>{sale.quantities[size]}</td>
                    <td>{remaining}</td>
                    <td>
                      <input
                        type="number"
                        min="0"
                        max={remaining}
                        value={shippedQuantities[size] || 0}
                        onChange={(e) => handleShippedChange(size, e.target.value)}
                        className="form-control"
                      />
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </Table>
          <Button variant="success" onClick={handleShip} className="me-2" disabled={Object.values(shippedQuantities).every(q => q === 0)}>Ship</Button>
          <Button variant="primary" onClick={() => setIsEditing(true)} className="me-2">Edit</Button>
          <Button variant="danger" onClick={handleDelete}>Delete</Button>
        </>
      )}
    </Container>
  );
};

export default SalesDetail;
