import React, { useContext } from 'react';
import { Container, Form, Table, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { AppContext } from '../App';

const Sales = () => {
  const { sales, searchTerm, setSearchTerm, setSales, setAlertMessage } = useContext(AppContext);
  const navigate = useNavigate();

  const filteredSales = sales.filter(
    (sale) =>
      sale.customer.toLowerCase().includes(searchTerm.toLowerCase()) ||
      sale.plate.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleShip = (id) => {
    setSales(sales.map(sale => {
      if (sale.id === id && !sale.shipped) {
        const updatedSale = { ...sale };
        Object.keys(sale.quantities).forEach(size => {
          const toShip = sale.quantities[size] - (sale.shippedQuantities?.[size] || 0);
          if (toShip > 0) {
            updatedSale.shippedQuantities = updatedSale.shippedQuantities || {};
            updatedSale.shippedQuantities[size] = (updatedSale.shippedQuantities[size] || 0) + toShip;
            updatedSale.quantities[size] -= toShip;
            if (updatedSale.quantities[size] === 0) updatedSale.shipped = true;
          }
        });
        return updatedSale;
      }
      return sale;
    }));
    setAlertMessage('Shipped and subtracted from store');
    setTimeout(() => setAlertMessage(''), 3000);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this sale?')) {
      setSales(sales.filter(sale => sale.id !== id));
      setAlertMessage('Sale deleted');
      setTimeout(() => setAlertMessage(''), 3000);
    }
  };

  return (
    <Container className="mt-5">
      <h2>Sales</h2>
      <Form.Group className="mb-3">
        <Form.Label>Search by Customer or Plate Number</Form.Label>
        <Form.Control type="text" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
      </Form.Group>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer</th>
            <th>Plate</th>
            <th>FS</th>
            <th>8mm (O/R)</th>
            <th>10mm (O/R)</th>
            <th>12mm (O/R)</th>
            <th>14mm (O/R)</th>
            <th>16mm (O/R)</th>
            <th>20mm (O/R)</th>
            <th>24mm (O/R)</th>
            <th>32mm (O/R)</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredSales.map((sale) => (
            <tr key={sale.id} onClick={() => navigate(`/sales/${sale.id}`)} style={{ cursor: 'pointer' }}>
              <td>{sale.date}</td>
              <td>{sale.customer}</td>
              <td>{sale.plate}</td>
              <td>{sale.fs}</td>
              {Object.keys(sale.quantities).map((size) => (
                <td key={size}>
                  {sale.quantities[size]} / {sale.quantities[size] - (sale.shippedQuantities?.[size] || 0)}
                </td>
              ))}
              <td>{sale.shipped ? 'Shipped' : 'Pending'}</td>
              <td>
                {!sale.shipped && (
                  <>
                    <Button variant="success" onClick={(e) => { e.stopPropagation(); handleShip(sale.id); }} className="me-2">Ship</Button>
                    <Button variant="danger" onClick={(e) => { e.stopPropagation(); handleDelete(sale.id); }}>Delete</Button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default Sales;
