import React, { useState, useContext } from 'react';
import { Container, Form, Button, Alert, Row, Col } from 'react-bootstrap';
import { AppContext } from '../App';

const AddPurchase = () => {
  const { purchases, setPurchases, setAlertMessage } = useContext(AppContext);
  const [formData, setFormData] = useState({
    quantities: { '8mm': 0, '10mm': 0, '12mm': 0, '14mm': 0, '16mm': 0, '20mm': 0, '24mm': 0, '32mm': 0 },
    date: new Date().toISOString().split('T')[0],
  });

  const handleQuantityChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      quantities: { ...formData.quantities, [name]: parseInt(value) || 0 },
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setPurchases([...purchases, { ...formData, id: Date.now() }]);
    setAlertMessage('Purchase Added');
    setTimeout(() => setAlertMessage(''), 3000);
    setFormData({
      quantities: { '8mm': 0, '10mm': 0, '12mm': 0, '14mm': 0, '16mm': 0, '20mm': 0, '24mm': 0, '32mm': 0 },
      date: new Date().toISOString().split('T')[0],
    });
  };

  return (
    <Container className="mt-5">
      <h2>Add Purchase</h2>
      
      <Form onSubmit={handleSubmit}>
        <Row>
          {Object.keys(formData.quantities).map((size) => (
            <Col md={3} key={size}>
              <Form.Group>
                <Form.Label>{size}</Form.Label>
                <Form.Control type="number" name={size} value={formData.quantities[size]} onChange={handleQuantityChange} min="0" />
              </Form.Group>
            </Col>
          ))}
        </Row>
        <Form.Group className="mt-3">
          <Form.Label>Date</Form.Label>
          <Form.Control type="date" name="date" value={formData.date} onChange={handleChange} required />
        </Form.Group>
        <Button type="submit" variant="primary" className="mt-3">Submit</Button>
      </Form>
    </Container>
  );
};

export default AddPurchase;
