import React, { useContext } from 'react';
import { Container, Table } from 'react-bootstrap';
import { AppContext } from '../App';

const Remaining = () => {
  const { sales } = useContext(AppContext);

  return (
    <Container className="mt-5">
      <h2>Remaining</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer</th>
            <th>Plate</th>
            <th>FS</th>
            <th>8mm</th>
            <th>10mm</th>
            <th>12mm</th>
            <th>14mm</th>
            <th>16mm</th>
            <th>20mm</th>
            <th>24mm</th>
            <th>32mm</th>
          </tr>
        </thead>
        <tbody>
          {sales.map((sale) => {
            // Calculate remaining quantities for each size
            const remainingQuantities = Object.keys(sale.quantities).reduce((acc, size) => {
              const shipped = sale.shippedQuantities?.[size] || 0;
              const remaining = sale.quantities[size] - shipped;
              acc[size] = remaining > 0 ? remaining : 0;
              return acc;
            }, {});

            // Check if there are any remaining quantities
            const hasRemaining = Object.values(remainingQuantities).some(remaining => remaining > 0);

            if (hasRemaining) {
              return (
                <tr key={sale.id}>
                  <td>{sale.date}</td>
                  <td>{sale.customer}</td>
                  <td>{sale.plate}</td>
                  <td>{sale.fs}</td>
                  {Object.keys(sale.quantities).map((size) => (
                    <td key={size}>{remainingQuantities[size]}</td>
                  ))}
                </tr>
              );
            }
            return null;
          })}
        </tbody>
      </Table>
    </Container>
  );
};

export default Remaining;
