import React, { useState, useContext } from 'react';
import { Container, Table, Button, Row, Col } from 'react-bootstrap';
import { AppContext } from '../App';

const AddSales = () => {
  const { sales, setSales, setAlertMessage } = useContext(AppContext);
  const [formData, setFormData] = useState({
    customer: '',
    plate: '',
    fs: '',
    quantities: { '8mm': 0, '10mm': 0, '12mm': 0, '14mm': 0, '16mm': 0, '20mm': 0, '24mm': 0, '32mm': 0 },
    date: new Date().toISOString().split('T')[0],
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleQuantityChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      quantities: { ...formData.quantities, [name]: parseInt(value) || 0 },
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSales([...sales, { ...formData, id: Date.now(), shipped: false }]);
    setAlertMessage('Add Successfully'); // Set success message
    setTimeout(() => setAlertMessage(''), 3000); // Clear after 3 seconds
    setFormData({
      customer: '',
      plate: '',
      fs: '',
      quantities: { '8mm': 0, '10mm': 0, '12mm': 0, '14mm': 0, '16mm': 0, '20mm': 0, '24mm': 0, '32mm': 0 },
      date: new Date().toISOString().split('T')[0],
    });
  };

  return (
    <Container className="mt-5">
      <h2>Add Sales</h2>
      <form onSubmit={handleSubmit}>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Customer Name</th>
              <th>Plate Number</th>
              <th>FS Number</th>
              <th>8mm</th>
              <th>10mm</th>
              <th>12mm</th>
              <th>14mm</th>
              <th>16mm</th>
              <th>20mm</th>
              <th>24mm</th>
              <th>32mm</th>
              <th>Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>
                <input
                  type="text"
                  name="customer"
                  value={formData.customer}
                  onChange={handleChange}
                  required
                  className="form-control"
                />
              </td>
              <td>
                <input
                  type="text"
                  name="plate"
                  value={formData.plate}
                  onChange={handleChange}
                  required
                  className="form-control"
                />
              </td>
              <td>
                <input
                  type="text"
                  name="fs"
                  value={formData.fs}
                  onChange={handleChange}
                  required
                  className="form-control"
                />
              </td>
              {Object.keys(formData.quantities).map((size) => (
                <td key={size}>
                  <input
                    type="number"
                    name={size}
                    value={formData.quantities[size]}
                    onChange={handleQuantityChange}
                    min="0"
                    className="form-control"
                  />
                </td>
              ))}
              <td>
                <input
                  type="date"
                  name="date"
                  value={formData.date}
                  onChange={handleChange}
                  required
                  className="form-control"
                />
              </td>
              <td>
                <Button type="submit" variant="primary">Submit</Button>
              </td>
            </tr>
          </tbody>
        </Table>
      </form>
      {alertMessage && <p className="text-success mt-3">{alertMessage}</p>} {/* Success message as paragraph */}
    </Container>
  );
};

export default AddSales;
