import React, { useState, useContext } from 'react';
import { Container, Form, Button, Row, Col, Alert } from 'react-bootstrap';
import { AppContext } from '../App';

const AddSales = () => {
  const { setSales, setAlertMessage, token, API_BASE, fetchSales } = useContext(AppContext);
  const [formData, setFormData] = useState({
    customer: '',
    plate: '',
    fs: '',
    quantities: { '8mm': 0, '10mm': 0, '12mm': 0, '14mm': 0, '16mm': 0, '20mm': 0, '24mm': 0, '32mm': 0 },
    date: new Date().toISOString().split('T')[0],
  });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleQuantityChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      quantities: { ...formData.quantities, [name]: parseInt(value) || 0 },
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    // Filter out zero quantities
    const filledQuantities = Object.fromEntries(
      Object.entries(formData.quantities).filter(([k, v]) => v > 0)
    );

    if (Object.keys(filledQuantities).length === 0) {
      setMessage('❌ Please enter at least one quantity');
      setTimeout(() => setMessage(''), 3000);
      setLoading(false);
      return;
    }

    if (!formData.customer.trim()) {
      setMessage('❌ Please enter customer name');
      setTimeout(() => setMessage(''), 3000);
      setLoading(false);
      return;
    }

    try {
      const response = await fetch(`${API_BASE}/sales`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          ...formData,
          quantities: filledQuantities
        }),
      });

      if (response.ok) {
        const newSale = await response.json();
        setMessage('✅ Registered To Sales - Thank you!');
        setAlertMessage('✅ Sales registered successfully!');
        
        // Refresh sales data
        await fetchSales();
        
        setTimeout(() => {
          setMessage('');
          setAlertMessage('');
        }, 3000);
        
        // Reset form
        setFormData({
          customer: '',
          plate: '',
          fs: '',
          quantities: { '8mm': 0, '10mm': 0, '12mm': 0, '14mm': 0, '16mm': 0, '20mm': 0, '24mm': 0, '32mm': 0 },
          date: new Date().toISOString().split('T')[0],
        });
      } else {
        setMessage('❌ Failed to register sale');
      }
    } catch (error) {
      setMessage('❌ Network error');
    }
    
    setLoading(false);
  };

  return (
    <Container className="mt-5">
      <h2>Add Sales</h2>
      {message && <Alert variant={message.includes('❌') ? 'danger' : 'success'}>{message}</Alert>}
      
      <Form onSubmit={handleSubmit}>
        <Row>
          <Col md={4}>
            <Form.Group>
              <Form.Label>Customer Name *</Form.Label>
              <Form.Control 
                type="text" 
                name="customer" 
                value={formData.customer} 
                onChange={handleChange} 
                required 
              />
            </Form.Group>
          </Col>
          <Col md={4}>
            <Form.Group>
              <Form.Label>Plate Number *</Form.Label>
              <Form.Control 
                type="text" 
                name="plate" 
                value={formData.plate} 
                onChange={handleChange} 
                required 
              />
            </Form.Group>
          </Col>
          <Col md={4}>
            <Form.Group>
              <Form.Label>FS Number *</Form.Label>
              <Form.Control 
                type="text" 
                name="fs" 
                value={formData.fs} 
                onChange={handleChange} 
                required 
              />
            </Form.Group>
          </Col>
        </Row>
        
        <Row className="mt-3">
          <h5>Enter Quantities</h5>
          {Object.keys(formData.quantities).map((size) => (
            <Col md={3} key={size}>
              <Form.Group>
                <Form.Label>{size}</Form.Label>
                <Form.Control 
                  type="number" 
                  name={size} 
                  value={formData.quantities[size]} 
                  onChange={handleQuantityChange} 
                  min="0" 
                />
              </Form.Group>
            </Col>
          ))}
        </Row>
        
        <Form.Group className="mt-3">
          <Form.Label>Date *</Form.Label>
          <Form.Control 
            type="date" 
            name="date" 
            value={formData.date} 
            onChange={handleChange} 
            required 
          />
        </Form.Group>
        
        <Button 
          type="submit" 
          variant="primary" 
          className="mt-3"
          disabled={loading}
        >
          {loading ? 'Submitting...' : 'Submit'}
        </Button>
      </Form>
    </Container>
  );
};

export default AddSales;
