import React, { useContext } from 'react';
import { Container, Table, Button } from 'react-bootstrap';
import { AppContext } from '../App';

const Purchase = () => {
  const { purchases, setPurchases, setAlertMessage } = useContext(AppContext);

  const purchasesByDate = purchases.reduce((acc, purchase) => {
    if (!acc[purchase.date]) {
      acc[purchase.date] = { ...purchase.quantities };
    } else {
      Object.keys(purchase.quantities).forEach(size => {
        acc[purchase.date][size] += purchase.quantities[size];
      });
    }
    return acc;
  }, {});

  const handleDelete = (date) => {
    if (window.confirm('Are you sure you want to delete this purchase?')) {
      const purchaseToDelete = purchases.find(p => p.date === date);
      if (purchaseToDelete) {
        setPurchases(prevPurchases => prevPurchases.filter(p => p.id !== purchaseToDelete.id));
        setAlertMessage('Purchase deleted');
        setTimeout(() => setAlertMessage(''), 3000);
      }
    }
  };

  return (
    <Container className="mt-5">
      <h2>Purchase</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Date</th>
            <th>8mm</th>
            <th>10mm</th>
            <th>12mm</th>
            <th>14mm</th>
            <th>16mm</th>
            <th>20mm</th>
            <th>24mm</th>
            <th>32mm</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(purchasesByDate).sort().map((date) => (
            <tr key={date}>
              <td>{date}</td>
              {Object.keys(purchasesByDate[date]).map((size) => (
                <td key={size}>{purchasesByDate[date][size]}</td>
              ))}
              <td>
                <Button variant="danger" onClick={() => handleDelete(date)}>Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default Purchase;
