import React, { useContext } from 'react';
import { Container, Form, Table, Button, Alert } from 'react-bootstrap';
import { AppContext } from '../App';

const Sales = () => {
  const { sales, setSales, searchTerm, setSearchTerm, setAlertMessage, balances } = useContext(AppContext);

  const filteredSales = sales.filter(
    (sale) =>
      sale.customer.toLowerCase().includes(searchTerm.toLowerCase()) ||
      sale.date.includes(searchTerm)
  );

  const handleShip = (id, shippedQuantities) => {
    setSales(sales.map((sale) => {
      if (sale.id === id) {
        const updatedSale = { ...sale };
        Object.keys(sale.quantities).forEach(size => {
          const shipped = shippedQuantities[size] || 0;
          const ordered = sale.quantities[size] || 0;
          if (shipped <= ordered) {
            updatedSale.shippedQuantities = updatedSale.shippedQuantities || {};
            updatedSale.shippedQuantities[size] = (updatedSale.shippedQuantities[size] || 0) + shipped;
            updatedSale.quantities[size] = ordered - shipped; // Remaining quantity
            if (shipped > 0) {
              updatedSale.shipped = true; // Mark as shipped if any quantity is shipped
            }
          }
        });
        return updatedSale;
      }
      return sale;
    }));
    setAlertMessage('Shipped and subtracted from store');
    setTimeout(() => setAlertMessage(''), 3000);
  };

  const handleShippedChange = (id, size, value) => {
    const shipped = parseInt(value) || 0;
    setSales(sales.map((sale) => {
      if (sale.id === id) {
        const updatedSale = { ...sale };
        const ordered = sale.quantities[size] || 0;
        if (shipped <= ordered) {
          updatedSale.shippedQuantities = updatedSale.shippedQuantities || {};
          updatedSale.shippedQuantities[size] = shipped;
        }
        return updatedSale;
      }
      return sale;
    }));
  };

  return (
    <Container className="mt-5">
      <h2>Sales</h2>
      {alertMessage && <Alert variant="success">{alertMessage}</Alert>}
      <Form.Group>
        <Form.Label>Search by Customer or Date</Form.Label>
        <Form.Control type="text" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
      </Form.Group>
      <Table striped bordered hover className="mt-3">
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer</th>
            <th>Plate</th>
            <th>FS</th>
            <th>8mm (Ordered/Remaining)</th>
            <th>10mm (Ordered/Remaining)</th>
            <th>12mm (Ordered/Remaining)</th>
            <th>14mm (Ordered/Remaining)</th>
            <th>16mm (Ordered/Remaining)</th>
            <th>20mm (Ordered/Remaining)</th>
            <th>24mm (Ordered/Remaining)</th>
            <th>32mm (Ordered/Remaining)</th>
            <th>Shipped (Input)</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredSales.map((sale) => (
            <tr key={sale.id} onClick={() => alert('Enter shipped quantities and click Ship to subtract from store.')}>
              <td>{sale.date}</td>
              <td>{sale.customer}</td>
              <td>{sale.plate}</td>
              <td>{sale.fs}</td>
              {Object.keys(sale.quantities).map((size) => (
                <td key={size}>
                  {sale.quantities[size]} / {sale.quantities[size] - (sale.shippedQuantities?.[size] || 0)}
                </td>
              ))}
              <td>
                <input
                  type="number"
                  min="0"
                  max={sale.quantities['8mm'] || 0}
                  value={sale.shippedQuantities?.['8mm'] || 0}
                  onChange={(e) => handleShippedChange(sale.id, '8mm', e.target.value)}
                  className="form-control"
                />
                <input
                  type="number"
                  min="0"
                  max={sale.quantities['10mm'] || 0}
                  value={sale.shippedQuantities?.['10mm'] || 0}
                  onChange={(e) => handleShippedChange(sale.id, '10mm', e.target.value)}
                  className="form-control mt-1"
                />
                <input
                  type="number"
                  min="0"
                  max={sale.quantities['12mm'] || 0}
                  value={sale.shippedQuantities?.['12mm'] || 0}
                  onChange={(e) => handleShippedChange(sale.id, '12mm', e.target.value)}
                  className="form-control mt-1"
                />
                <input
                  type="number"
                  min="0"
                  max={sale.quantities['14mm'] || 0}
                  value={sale.shippedQuantities?.['14mm'] || 0}
                  onChange={(e) => handleShippedChange(sale.id, '14mm', e.target.value)}
                  className="form-control mt-1"
                />
                <input
                  type="number"
                  min="0"
                  max={sale.quantities['16mm'] || 0}
                  value={sale.shippedQuantities?.['16mm'] || 0}
                  onChange={(e) => handleShippedChange(sale.id, '16mm', e.target.value)}
                  className="form-control mt-1"
                />
                <input
                  type="number"
                  min="0"
                  max={sale.quantities['20mm'] || 0}
                  value={sale.shippedQuantities?.['20mm'] || 0}
                  onChange={(e) => handleShippedChange(sale.id, '20mm', e.target.value)}
                  className="form-control mt-1"
                />
                <input
                  type="number"
                  min="0"
                  max={sale.quantities['24mm'] || 0}
                  value={sale.shippedQuantities?.['24mm'] || 0}
                  onChange={(e) => handleShippedChange(sale.id, '24mm', e.target.value)}
                  className="form-control mt-1"
                />
                <input
                  type="number"
                  min="0"
                  max={sale.quantities['32mm'] || 0}
                  value={sale.shippedQuantities?.['32mm'] || 0}
                  onChange={(e) => handleShippedChange(sale.id, '32mm', e.target.value)}
                  className="form-control mt-1"
                />
              </td>
              <td>{sale.shipped ? 'Shipped' : 'Pending'}</td>
              <td>
                {!sale.shipped && (
                  <Button variant="success" onClick={() => handleShip(sale.id, sale.shippedQuantities)}>Ship</Button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default Sales;
