import React, { useContext } from 'react';
import { Container, Table } from 'react-bootstrap';
import { AppContext } from '../App';

const Remaining = () => {
  const { sales } = useContext(AppContext);

  return (
    <Container className="mt-5">
      <h2>Remaining</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer</th>
            <th>Plate</th>
            <th>FS</th>
            <th>8mm</th>
            <th>10mm</th>
            <th>12mm</th>
            <th>14mm</th>
            <th>16mm</th>
            <th>20mm</th>
            <th>24mm</th>
            <th>32mm</th>
          </tr>
        </thead>
        <tbody>
          {sales.map((sale) => {
            const hasRemaining = Object.keys(sale.quantities).some(size => {
              const remaining = sale.quantities[size] - (sale.shippedQuantities?.[size] || 0);
              return remaining > 0;
            });
            if (hasRemaining) {
              return (
                <tr key={sale.id}>
                  <td>{sale.date}</td>
                  <td>{sale.customer}</td>
                  <td>{sale.plate}</td>
                  <td>{sale.fs}</td>
                  {Object.keys(sale.quantities).map((size) => (
                    <td key={size}>
                      {sale.quantities[size] - (sale.shippedQuantities?.[size] || 0)}
                    </td>
                  ))}
                </tr>
              );
            }
            return null;
          })}
        </tbody>
      </Table>
    </Container>
  );
};

export default Remaining;
