import React, { useContext } from 'react';
import { Container, Table } from 'react-bootstrap';
import { AppContext } from '../App';

const TotalSales = () => {
  const { soldTotals } = useContext(AppContext);

  return (
    <Container className="mt-5">
      <h2>Total Sales</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Size</th>
            <th>Total Shipped</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(soldTotals).map((size) => (
            <tr key={size}>
              <td>{size}</td>
              <td>{soldTotals[size]}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default TotalSales;
