import React, { useContext } from 'react';
import { Container, Form, Table, Button, Alert } from 'react-bootstrap';
import { AppContext } from '../App';

const Sales = () => {
  const { sales, setSales, searchTerm, setSearchTerm, setAlertMessage } = useContext(AppContext);

  const filteredSales = sales.filter(
    (sale) =>
      sale.customer.toLowerCase().includes(searchTerm.toLowerCase()) ||
      sale.date.includes(searchTerm)
  );

  const handleShip = (id) => {
    setSales(sales.map((sale) => sale.id === id ? { ...sale, shipped: true } : sale));
    setAlertMessage('Shipped and subtracted from store');
    setTimeout(() => setAlertMessage(''), 3000);
  };

  return (
    <Container className="mt-5">
      <h2>Sales</h2>
      {alertMessage && <Alert variant="success">{alertMessage}</Alert>}
      <Form.Group>
        <Form.Label>Search by Customer or Date</Form.Label>
        <Form.Control type="text" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
      </Form.Group>
      <Table striped bordered hover className="mt-3">
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer</th>
            <th>Plate</th>
            <th>FS</th>
            <th>8mm</th>
            <th>10mm</th>
            <th>12mm</th>
            <th>14mm</th>
            <th>16mm</th>
            <th>20mm</th>
            <th>24mm</th>
            <th>32mm</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredSales.map((sale) => (
            <tr key={sale.id} onClick={() => alert('Click Ship to subtract from store.')}>
              <td>{sale.date}</td>
              <td>{sale.customer}</td>
              <td>{sale.plate}</td>
              <td>{sale.fs}</td>
              {Object.keys(sale.quantities).map((size) => (
                <td key={size}>{sale.quantities[size]}</td>
              ))}
              <td>{sale.shipped ? 'Shipped' : 'Pending'}</td>
              <td>
                {!sale.shipped && (
                  <Button variant="success" onClick={() => handleShip(sale.id)}>Ship</Button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default Sales;
