import React, { createContext, useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Navbar, Nav, Container, Alert } from 'react-bootstrap';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Home from './pages/Home';
import AddSales from './pages/AddSales';
import Sales from './pages/Sales';
import SalesDetail from './pages/SalesDetail';
import TotalSales from './pages/TotalSales';
import AddPurchase from './pages/AddPurchase';
import Purchase from './pages/Purchase';
import TotalPurchase from './pages/TotalPurchase';
import Remaining from './pages/Remaining';
import StoreBalance from './pages/StoreBalance';
import FactoryPurchased from './pages/AddFactoryPurchased';
import Factory from './pages/Factory';
import Stock from './pages/Stock';
import SalesAvailable from './pages/SalesAvialable';
import Login from './pages/Login';
import ChangePassword from './pages/ChangePassword';

export const AppContext = createContext();

const App = () => {
  const [sales, setSales] = useState([]);
  const [purchases, setPurchases] = useState([]);
  const [factoryPurchases, setFactoryPurchases] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [alertMessage, setAlertMessage] = useState('');
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));

  // API base URL
  const API_BASE = 'http://localhost:5000/api';

  // Auth functions
  const login = async (username, password) => {
    try {
      const response = await fetch(`${API_BASE}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (response.ok) {
        setToken(data.token);
        setUser(data.user);
        localStorage.setItem('token', data.token);
        return { success: true };
      } else {
        return { success: false, message: data.message };
      }
    } catch (error) {
      return { success: false, message: 'Network error' };
    }
  };

  const logout = () => {
    setToken(null);
    setUser(null);
    localStorage.removeItem('token');
    setSales([]);
    setPurchases([]);
    setFactoryPurchases([]);
  };

  const changePassword = async (currentPassword, newPassword) => {
    try {
      const response = await fetch(`${API_BASE}/change-password`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ currentPassword, newPassword }),
      });

      const data = await response.json();
      return data;
    } catch (error) {
      return { message: 'Network error' };
    }
  };

  // Fetch data functions
  const fetchSales = async () => {
    if (!token) return;

    try {
      const response = await fetch(`${API_BASE}/sales`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setSales(data);
      }
    } catch (error) {
      console.error('Error fetching sales:', error);
    }
  };

  const fetchPurchases = async () => {
    if (!token) return;

    try {
      const response = await fetch(`${API_BASE}/purchases`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setPurchases(data);
      }
    } catch (error) {
      console.error('Error fetching purchases:', error);
    }
  };

  const fetchFactoryPurchases = async () => {
    if (!token) return;

    try {
      const response = await fetch(`${API_BASE}/factory-purchases`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setFactoryPurchases(data);
      }
    } catch (error) {
      console.error('Error fetching factory purchases:', error);
    }
  };

  // Load user data on app start
  useEffect(() => {
    if (token) {
      // Verify token and get user data
      const verifyToken = async () => {
        try {
          const response = await fetch(`${API_BASE}/sales`, {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          });

          if (response.ok) {
            // Token is valid, fetch all data
            fetchSales();
            fetchPurchases();
            fetchFactoryPurchases();

            // Decode token to get user info (simplified - in real app, verify with backend)
            const tokenData = JSON.parse(atob(token.split('.')[1]));
            setUser({
              id: tokenData.userId,
              username: tokenData.username,
              role: tokenData.role
            });
          } else {
            logout();
          }
        } catch (error) {
          console.error('Token verification failed:', error);
          logout();
        }
      };

      verifyToken();
    }
  }, [token]);

  // Calculate totals
  const calculateTotals = (items, includeShippedOnly = false) => {
    const sizes = ['8mm', '10mm', '12mm', '14mm', '16mm', '20mm', '24mm', '32mm'];
    return sizes.reduce((acc, size) => {
      acc[size] = items.reduce((sum, item) => {
        if (!includeShippedOnly) {
          return sum + (item.quantities?.[size] || 0);
        } else {
          const shipped = item.shippedQuantities?.[size] || 0;
          return sum + shipped;
        }
      }, 0);
      return acc;
    }, {});
  };

  const purchasedTotals = calculateTotals(purchases, false);
  const soldTotals = calculateTotals(sales, true);
  const factoryStockTotals = calculateTotals(factoryPurchases, false);

  const balances = Object.keys(purchasedTotals).reduce((acc, size) => {
    acc[size] = Math.max(0, purchasedTotals[size] - (soldTotals[size] || 0));
    return acc;
  }, {});

  // Check permissions based on user role
  const canAccess = (requiredRole) => {
    if (!user) return false;
    if (user.role === 'admin') return true;
    return user.role === requiredRole;
  };

  if (!token) {
    return <Login onLogin={login} />;
  }

  return (
    <AppContext.Provider value={{ 
      sales, setSales, 
      purchases, setPurchases, 
      factoryPurchases, setFactoryPurchases,
      searchTerm, setSearchTerm, 
      alertMessage, setAlertMessage, 
      purchasedTotals, soldTotals, 
      factoryStockTotals,
      balances,
      user,
      token,
      API_BASE,
      fetchSales,
      fetchPurchases,
      fetchFactoryPurchases,
      canAccess
    }}>
      <Router>
        <Navbar bg="light" expand="lg" className="py-2 border-bottom">
          <Container>
            <Navbar.Brand style={{color:'blue'}}>
              Rebar Management - {user?.username} ({user?.role})
            </Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav" className="justify-content-center">
              <Nav className="mx-auto">
                <Nav.Link as={Navigate} to="/">Home</Nav.Link>

                {/* Sales-related navigation */}
                {(canAccess('admin') || canAccess('user')) && (
                  <>
                    <Nav.Link as={Navigate} to="/add-sales">Add Sales</Nav.Link>
                    <Nav.Link as={Navigate} to="/sales">Sales</Nav.Link>
                    <Nav.Link as={Navigate} to="/total-sales">Total Sales</Nav.Link>
                  </>
                )}

                {/* Purchase-related navigation */}
                {canAccess('user') && (
                  <>
                    <Nav.Link as={Navigate} to="/add-purchase">Add Imported Purchase</Nav.Link>
                    <Nav.Link as={Navigate} to="/purchase">Purchase Imported to Store</Nav.Link>
                    <Nav.Link as={Navigate} to="/total-purchase">Total Imported Purchase</Nav.Link>
                  </>
                )}

                {/* Common navigation for both roles */}
                <Nav.Link as={Navigate} to="/remaining">Remaining</Nav.Link>
                <Nav.Link as={Navigate} to="/store">Currently Store (Balance)</Nav.Link>
                <Nav.Link as={Navigate} to="/sales-available">Sales Available</Nav.Link>

                {/* Admin-only navigation */}
                {canAccess('admin') && (
                  <>
                    <Nav.Link as={Navigate} to="/factory-purchased">Factory Purchase</Nav.Link>
                    <Nav.Link as={Navigate} to="/factory">Factory</Nav.Link>
                    <Nav.Link as={Navigate} to="/stock">Stock</Nav.Link>
                  </>
                )}

                {/* User management */}
                <Nav.Link as={Navigate} to="/change-password">Change Password</Nav.Link>
                <Nav.Link onClick={logout}>Logout</Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>

        {alertMessage && (
          <Alert variant={alertMessage.includes('❌') ? 'danger' : 'success'} className="m-3">
            {alertMessage}
          </Alert>
        )}

        <Routes>
          <Route path="/login" element={<Navigate to="/" />} />
          <Route path="/" element={<Home />} />

          {/* Sales routes */}
          <Route path="/add-sales" element={canAccess('admin') || canAccess('user') ? <AddSales /> : <Navigate to="/" />} />
          <Route path="/sales" element={canAccess('admin') || canAccess('user') ? <Sales /> : <Navigate to="/" />} />
          <Route path="/sales/:id" element={canAccess('admin') || canAccess('user') ? <SalesDetail /> : <Navigate to="/" />} />
          <Route path="/total-sales" element={canAccess('admin') || canAccess('user') ? <TotalSales /> : <Navigate to="/" />} />

          {/* Purchase routes */}
          <Route path="/add-purchase" element={canAccess('user') ? <AddPurchase /> : <Navigate to="/" />} />
          <Route path="/purchase" element={canAccess('user') ? <Purchase/> : <Navigate to="/" />} />
          <Route path="/total-purchase" element={canAccess('user') ? <TotalPurchase /> : <Navigate to="/" />} />

          {/* Common routes */}
          <Route path="/remaining" element={<Remaining />} />
          <Route path="/store" element={<StoreBalance />} />
          <Route path="/sales-available" element={<SalesAvailable />} />
          <Route path="/change-password" element={<ChangePassword />} />

          {/* Admin-only routes */}
          <Route path="/factory-purchased" element={canAccess('admin') ? <FactoryPurchased /> : <Navigate to="/" />} />
          <Route path="/factory" element={canAccess('admin') ? <Factory /> : <Navigate to="/" />} />
          <Route path="/stock" element={canAccess('admin') ? <Stock /> : <Navigate to="/" />} />
        </Routes>
      </Router>
    </AppContext.Provider>
  );
};

export default App;
