import React, { useContext, useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Table, Button, Alert } from 'react-bootstrap';
import { AppContext } from '../App';

const Detail = () => {
  const { id } = useParams();
  const { sales, setSales, setAlertMessage } = useContext(AppContext);
  const navigate = useNavigate();
  const sale = sales.find(s => s.id === parseInt(id));
  const [shippedQuantities, setShippedQuantities] = useState({});

  useEffect(() => {
    if (sale) {
      setShippedQuantities(sale.shippedQuantities || {});
    }
  }, [sale]);

  const handleShippedChange = (size, value) => {
    const shipped = parseInt(value) || 0;
    const ordered = sale.quantities[size] || 0;
    if (shipped <= ordered) {
      setShippedQuantities({ ...shippedQuantities, [size]: shipped });
    }
  };

  const handleShip = () => {
    setSales(sales.map(s => {
      if (s.id === parseInt(id)) {
        const updatedSale = { ...s };
        Object.keys(shippedQuantities).forEach(size => {
          const shipped = shippedQuantities[size] || 0;
          const ordered = s.quantities[size] || 0;
          if (shipped <= ordered && shipped > 0) {
            updatedSale.shippedQuantities = updatedSale.shippedQuantities || {};
            updatedSale.shippedQuantities[size] = (updatedSale.shippedQuantities[size] || 0) + shipped;
            updatedSale.quantities[size] = ordered - updatedSale.shippedQuantities[size];
            if (updatedSale.quantities[size] === 0) {
              updatedSale.shipped = true;
            }
          }
        });
        return updatedSale;
      }
      return s;
    }));
    setAlertMessage('Shipped and subtracted from store');
    setTimeout(() => {
      setAlertMessage('');
      navigate('/sales');
    }, 3000);
  };

  if (!sale) return <Container><p>Sale not found</p></Container>;

  return (
    <Container className="mt-5">
      <h2>Sales Detail - {sale.customer}</h2>
      
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Size</th>
            <th>Ordered</th>
            <th>Shipped</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(sale.quantities).map((size) => (
            <tr key={size}>
              <td>{size}</td>
              <td>{sale.quantities[size]}</td>
              <td>
                <input
                  type="number"
                  min="0"
                  max={sale.quantities[size]}
                  value={shippedQuantities[size] || 0}
                  onChange={(e) => handleShippedChange(size, e.target.value)}
                  className="form-control"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Button variant="success" onClick={handleShip} className="mt-3">Ship</Button>
    </Container>
  );
};

export default SalesDetail;
